css:
.bower-install.tool-panel {
  overflow-y: scroll;
  padding: 10px;
  font-size: 11px;
  font-family: monospace;
  white-space: pre;
  height: 100px;
  background-color: #000;
  position: relative;
  border-top: 1px solid rgba(255, 255, 255, 0.07);
}
.bower-install .terminal {
  position: absolute;
  bottom: 10px;
}
.bower-install.overlay {
  transition: opacity 0.25s;
  text-align: center;
  opacity: 1;
}
.bower-install.overlay.fade-out {
  opacity: 0;
}
.bower-install.overlay.warning {
  color: #cc0000;
}
.bower-install.overlay.success {
  color: #26c26a;
}
